INSERT INTO hadiths (collection, book_number, hadith_number, narrator, arabic_text, english_translation, grade, reference) VALUES
('Jami at-Tirmidhi', 1, 20, 'Abu Said Al-Khudri', 'الْمَاءُ طَهُورٌ لاَ يُنَجِّسُهُ شَيْءٌ', 'Water is pure; nothing makes it impure.', 'sahih', 'Jami at-Tirmidhi 20'),
('Jami at-Tirmidhi', 1, 21, 'Abu Said Al-Khudri', 'إِنَّ الْمَاءَ طَهُورٌ لاَ يُنَجِّسُهُ شَيْءٌ', 'Indeed water is pure, nothing makes it impure.', 'sahih', 'Jami at-Tirmidhi 21'),
('Jami at-Tirmidhi', 1, 22, 'Abu Hurairah', 'هُوَ الطَّهُورُ مَاؤُهُ الْحِلُّ مَيْتَتُهُ', 'Its water is pure and its dead are lawful.', 'sahih', 'Jami at-Tirmidhi 22'),
('Jami at-Tirmidhi', 1, 23, 'Abu Umamah Al-Bahili', 'إِنَّ الْمَاءَ لاَ يُنَجِّسُهُ شَيْءٌ إِلاَّ مَا غَلَبَ عَلَى رِيحِهِ وَطَعْمِهِ وَلَوْنِهِ', 'Water is not made impure by anything, except what changes its smell, taste, or color.', 'hasan', 'Jami at-Tirmidhi 23'),
('Jami at-Tirmidhi', 1, 24, 'Kabshah bint Kab', 'إِنَّهَا لَيْسَتْ بِنَجَسٍ إِنَّمَا هِيَ مِنَ الطَّوَّافِينَ عَلَيْكُمْ وَالطَّوَّافَاتِ', 'It (the cat) is not impure. It is among those (males and females) who go around among you.', 'sahih', 'Jami at-Tirmidhi 24'),
('Jami at-Tirmidhi', 1, 25, 'Abu Hurairah', 'إِذَا اسْتَيْقَظَ أَحَدُكُمْ مِنْ نَوْمِهِ فَلاَ يَغْمِسْ يَدَهُ فِي الإِنَاءِ حَتَّى يَغْسِلَهَا ثَلاَثًا', 'When one of you wakes from sleep, he should not put his hand in the vessel until he washes it three times.', 'sahih', 'Jami at-Tirmidhi 25'),
('Jami at-Tirmidhi', 1, 26, 'Abdullah bin Umar', 'إِذَا كَانَ الْمَاءُ قُلَّتَيْنِ لَمْ يَحْمِلِ الْخَبَثَ', 'When the water reaches two Qullas, it does not carry impurity.', 'sahih', 'Jami at-Tirmidhi 26'),
('Jami at-Tirmidhi', 1, 27, 'Abu Hurairah', 'إِذَا وَقَعَ الذُّبَابُ فِي شَرَابِ أَحَدِكُمْ فَلْيَغْمِسْهُ ثُمَّ لِيَنْزِعْهُ', 'When a fly falls into the drink of one of you, then submerge it and then remove it.', 'sahih', 'Jami at-Tirmidhi 27'),
('Jami at-Tirmidhi', 1, 28, 'Ibn Abbas', 'اغْتَسَلَ بَعْضُ أَزْوَاجِ النَّبِيِّ فِي جَفْنَةٍ فَجَاءَ النَّبِيُّ يَتَوَضَّأُ مِنْهَا', 'One of the wives of the Prophet bathed from a vessel, and the Prophet came to perform Wudu from it.', 'sahih', 'Jami at-Tirmidhi 28'),
('Jami at-Tirmidhi', 1, 29, 'Humaid Al-Himyari', 'لَقِيتُ رَجُلاً صَحِبَ النَّبِيَّ أَرْبَعَ سِنِينَ كَمَا صَحِبَهُ أَبُو هُرَيْرَةَ', 'I met a man who accompanied the Prophet for four years as Abu Hurairah accompanied him.', 'hasan', 'Jami at-Tirmidhi 29'),
('Jami at-Tirmidhi', 1, 30, 'Abu Hurairah', 'لَوْلاَ أَنْ أَشُقَّ عَلَى أُمَّتِي لأَمَرْتُهُمْ بِالسِّوَاكِ مَعَ كُلِّ وُضُوءٍ', 'If I did not fear burdening my Ummah, I would have ordered them to use the Siwak with every Wudu.', 'sahih', 'Jami at-Tirmidhi 30'),
('Jami at-Tirmidhi', 1, 31, 'Abu Hurairah', 'لَوْلاَ أَنْ أَشُقَّ عَلَى أُمَّتِي لأَمَرْتُهُمْ بِالسِّوَاكِ عِنْدَ كُلِّ صَلاَةٍ', 'If I did not fear burdening my Ummah, I would have ordered them to use the Siwak for every Salat.', 'sahih', 'Jami at-Tirmidhi 31'),
('Jami at-Tirmidhi', 1, 32, 'Amir bin Rabiah', 'رَأَيْتُ النَّبِيَّ مَا لاَ أُحْصِي يَتَسَوَّكُ وَهُوَ صَائِمٌ', 'I saw the Prophet using the Siwak while fasting so many times that I cannot count.', 'daif', 'Jami at-Tirmidhi 32'),
('Jami at-Tirmidhi', 1, 33, 'Uthman bin Affan', 'كَانَ النَّبِيُّ يُخَلِّلُ لِحْيَتَهُ', 'The Prophet used to run his fingers through his beard.', 'hasan', 'Jami at-Tirmidhi 33'),
('Jami at-Tirmidhi', 1, 34, 'Ibn Abbas', 'نَهَى أَنْ يُتَنَفَّسَ فِي الإِنَاءِ أَوْ يُنْفَخَ فِيهِ', 'He prohibited breathing into the vessel or blowing into it.', 'sahih', 'Jami at-Tirmidhi 34'),
('Jami at-Tirmidhi', 1, 35, 'Abdullah bin Zaid', 'رَأَيْتُ النَّبِيَّ تَوَضَّأَ فَغَسَلَ وَجْهَهُ ثَلاَثًا وَيَدَيْهِ مَرَّتَيْنِ', 'I saw the Prophet perform Wudu, he washed his face three times and his hands twice.', 'sahih', 'Jami at-Tirmidhi 35'),
('Jami at-Tirmidhi', 1, 36, 'Abdullah bin Zaid', 'أَنَّ النَّبِيَّ مَسَحَ رَأْسَهُ بِيَدَيْهِ فَأَقْبَلَ بِهِمَا وَأَدْبَرَ', 'The Prophet wiped his head with his hands, going forward with them and backward.', 'sahih', 'Jami at-Tirmidhi 36'),
('Jami at-Tirmidhi', 1, 37, 'Ibn Abbas', 'أَنَّ النَّبِيَّ مَسَحَ بِرَأْسِهِ وَأُذُنَيْهِ ظَاهِرَهُمَا وَبَاطِنَهُمَا', 'The Prophet wiped his head and ears, their outside and inside.', 'hasan', 'Jami at-Tirmidhi 37'),
('Jami at-Tirmidhi', 1, 38, 'Rubayyi bint Muawwidh', 'رَأَيْتُ النَّبِيَّ يَتَوَضَّأُ فَمَسَحَ رَأْسَهُ مَرَّةً وَاحِدَةً', 'I saw the Prophet perform Wudu, and he wiped his head once.', 'sahih', 'Jami at-Tirmidhi 38'),
('Jami at-Tirmidhi', 1, 39, 'Abdullah bin Zaid', 'أَنَّ النَّبِيَّ أَخَذَ لأُذُنَيْهِ مَاءً خِلاَفَ الْمَاءِ الَّذِي أَخَذَ لِرَأْسِهِ', 'The Prophet took new water for his ears, other than the water he used for his head.', 'hasan', 'Jami at-Tirmidhi 39'),
('Jami at-Tirmidhi', 1, 40, 'Abdullah bin Amr', 'أَنَّ رَجُلاً تَوَضَّأَ فَتَرَكَ مَوْضِعَ ظُفُرٍ عَلَى قَدَمِهِ فَأَبْصَرَهُ النَّبِيُّ فَقَالَ ارْجِعْ فَأَحْسِنْ وُضُوءَكَ', 'A man performed Wudu leaving a spot the size of a nail on his foot. The Prophet saw it and said: Go back and perfect your Wudu.', 'sahih', 'Jami at-Tirmidhi 40'),
('Jami at-Tirmidhi', 1, 41, 'Al-Mughirah bin Shubah', 'تَوَضَّأَ النَّبِيُّ وَمَسَحَ عَلَى الْخُفَّيْنِ', 'The Prophet performed Wudu and wiped over his Khuffs.', 'sahih', 'Jami at-Tirmidhi 41'),
('Jami at-Tirmidhi', 1, 42, 'Safwan bin Assal', 'أَمَرَنَا رَسُولُ اللَّهِ أَنْ نَمْسَحَ عَلَى الْخُفَّيْنِ ثَلاَثَةَ أَيَّامٍ لِلْمُسَافِرِ وَيَوْمًا وَلَيْلَةً لِلْمُقِيمِ', 'The Messenger of Allah ordered us to wipe over the Khuffs: three days for the traveler and one day and night for the resident.', 'hasan', 'Jami at-Tirmidhi 42'),
('Jami at-Tirmidhi', 1, 43, 'Safwan bin Assal', 'كَانَ رَسُولُ اللَّهِ يَأْمُرُنَا إِذَا كُنَّا سَفْرًا أَنْ لاَ نَنْزِعَ خِفَافَنَا ثَلاَثَةَ أَيَّامٍ وَلَيَالِيَهُنَّ', 'The Messenger of Allah ordered us, when traveling, not to remove our Khuffs for three days and nights.', 'hasan', 'Jami at-Tirmidhi 43'),
('Jami at-Tirmidhi', 1, 44, 'Al-Mughirah bin Shubah', 'رَأَيْتُ النَّبِيَّ يَمْسَحُ عَلَى الْجَوْرَبَيْنِ وَالنَّعْلَيْنِ', 'I saw the Prophet wiping over his socks and sandals.', 'sahih', 'Jami at-Tirmidhi 44'),
('Jami at-Tirmidhi', 1, 45, 'Al-Mughirah bin Shubah', 'مَسَحَ النَّبِيُّ أَعْلَى الْخُفِّ وَأَسْفَلَهُ', 'The Prophet wiped over the top of the Khuff and its bottom.', 'daif', 'Jami at-Tirmidhi 45'),
('Jami at-Tirmidhi', 1, 46, 'Ibn Umar', 'تَوَضَّأَ النَّبِيُّ مَرَّةً مَرَّةً', 'The Prophet performed Wudu washing each part once.', 'sahih', 'Jami at-Tirmidhi 46'),
('Jami at-Tirmidhi', 1, 47, 'Abdullah bin Zaid', 'تَوَضَّأَ النَّبِيُّ مَرَّتَيْنِ مَرَّتَيْنِ', 'The Prophet performed Wudu washing each part twice.', 'sahih', 'Jami at-Tirmidhi 47'),
('Jami at-Tirmidhi', 1, 48, 'Uthman bin Affan', 'تَوَضَّأَ النَّبِيُّ ثَلاَثًا ثَلاَثًا', 'The Prophet performed Wudu washing each part three times.', 'sahih', 'Jami at-Tirmidhi 48'),
('Jami at-Tirmidhi', 1, 49, 'Aishah', 'كَانَ النَّبِيُّ يُعْجِبُهُ التَّيَمُّنُ فِي تَنَعُّلِهِ وَتَرَجُّلِهِ وَطُهُورِهِ وَفِي شَأْنِهِ كُلِّهِ', 'The Prophet liked to start with the right in putting on shoes, combing his hair, his purification, and in all his affairs.', 'sahih', 'Jami at-Tirmidhi 49'),
('Jami at-Tirmidhi', 1, 50, 'Uthman bin Affan', 'مَنْ تَوَضَّأَ فَأَحْسَنَ الْوُضُوءَ خَرَجَتْ خَطَايَاهُ مِنْ جَسَدِهِ حَتَّى تَخْرُجَ مِنْ تَحْتِ أَظْفَارِهِ', 'Whoever performs Wudu and does it well, his sins will leave his body, even from under his fingernails.', 'sahih', 'Jami at-Tirmidhi 50'),
('Jami at-Tirmidhi', 1, 51, 'Uthman bin Affan', 'رَأَيْتُ رَسُولَ اللَّهِ تَوَضَّأَ نَحْوَ وُضُوئِي هَذَا', 'I saw the Messenger of Allah perform Wudu like this Wudu of mine.', 'sahih', 'Jami at-Tirmidhi 51'),
('Jami at-Tirmidhi', 1, 52, 'Umar bin Al-Khattab', 'مَا مِنْكُمْ مِنْ أَحَدٍ يَتَوَضَّأُ فَيُبْلِغُ الْوُضُوءَ ثُمَّ يَقُولُ أَشْهَدُ أَنْ لاَ إِلَهَ إِلاَّ اللَّهُ', 'There is none among you who performs Wudu, doing it well, then says: I testify that none has the right to be worshipped except Allah.', 'sahih', 'Jami at-Tirmidhi 52'),
('Jami at-Tirmidhi', 1, 53, 'Ubayy bin Kab', 'كَانَ رَسُولُ اللَّهِ يَتَوَضَّأُ بِالْمُدِّ وَيَغْتَسِلُ بِالصَّاعِ', 'The Messenger of Allah would perform Wudu with a Mudd and Ghusl with a Sa.', 'sahih', 'Jami at-Tirmidhi 53'),
('Jami at-Tirmidhi', 1, 54, 'Abu Hurairah', 'تَوَضَّئُوا مِمَّا مَسَّتِ النَّارُ', 'Perform Wudu from that which fire has touched.', 'sahih', 'Jami at-Tirmidhi 54'),
('Jami at-Tirmidhi', 1, 55, 'Jabir bin Abdullah', 'كَانَ آخِرُ الأَمْرَيْنِ مِنْ رَسُولِ اللَّهِ تَرْكَ الْوُضُوءِ مِمَّا مَسَّتِ النَّارُ', 'The last of the two commands from the Messenger of Allah was leaving Wudu from what fire had touched.', 'sahih', 'Jami at-Tirmidhi 55'),
('Jami at-Tirmidhi', 1, 56, 'Ibn Abbas', 'كَانَ آخِرُ الأَمْرَيْنِ مِنْ رَسُولِ اللَّهِ تَرْكُ الْوُضُوءِ مِمَّا غَيَّرَتِ النَّارُ', 'The last of the two commands from the Messenger was leaving Wudu from what fire had altered.', 'sahih', 'Jami at-Tirmidhi 56'),
('Jami at-Tirmidhi', 1, 57, 'Al-Bara bin Azib', 'تَوَضَّئُوا مِنْ لُحُومِ الإِبِلِ وَلاَ تَتَوَضَّئُوا مِنْ لُحُومِ الْغَنَمِ', 'Perform Wudu from camel meat, and do not perform Wudu from mutton.', 'sahih', 'Jami at-Tirmidhi 57'),
('Jami at-Tirmidhi', 1, 58, 'Jabir bin Samurah', 'تَوَضَّئُوا مِنْ لُحُومِ الإِبِلِ', 'Perform Wudu from camel meat.', 'sahih', 'Jami at-Tirmidhi 58'),
('Jami at-Tirmidhi', 1, 59, 'Ali ibn Abi Talib', 'يُغْسَلُ مِنَ الْمَذْيِ وَيُتَوَضَّأُ', 'One washes from Al-Madhi and performs Wudu.', 'sahih', 'Jami at-Tirmidhi 59'),
('Jami at-Tirmidhi', 1, 60, 'Safwan bin Assal', 'كَانَ رَسُولُ اللَّهِ يَأْمُرُنَا إِذَا كُنَّا مُسَافِرِينَ أَنْ لاَ نَنْزِعَ خِفَافَنَا', 'The Messenger used to order us when traveling that we should not remove our Khuffs.', 'hasan', 'Jami at-Tirmidhi 60'),
('Jami at-Tirmidhi', 1, 61, 'Abu Hurairah', 'إِذَا وَجَدَ أَحَدُكُمْ فِي بَطْنِهِ شَيْئًا فَأَشْكَلَ عَلَيْهِ أَخَرَجَ مِنْهُ شَيْءٌ أَمْ لاَ فَلاَ يَخْرُجَنَّ مِنَ الْمَسْجِدِ حَتَّى يَسْمَعَ صَوْتًا أَوْ يَجِدَ رِيحًا', 'When one of you feels something in his stomach and is unsure whether something came out or not, he should not leave the masjid until he hears a sound or finds a smell.', 'sahih', 'Jami at-Tirmidhi 61'),
('Jami at-Tirmidhi', 1, 62, 'Ali ibn Abi Talib', 'الْعَيْنُ وِكَاءُ السَّهِ فَمَنْ نَامَ فَلْيَتَوَضَّأْ', 'The eye is the string of the anus. Whoever sleeps, let him perform Wudu.', 'hasan', 'Jami at-Tirmidhi 62'),
('Jami at-Tirmidhi', 1, 63, 'Ibn Abbas', 'إِنَّمَا الْوُضُوءُ عَلَى مَنْ نَامَ مُضْطَجِعًا', 'Wudu is only upon the one who sleeps lying down.', 'daif', 'Jami at-Tirmidhi 63'),
('Jami at-Tirmidhi', 1, 64, 'Busrah bint Safwan', 'مَنْ مَسَّ ذَكَرَهُ فَلْيَتَوَضَّأْ', 'Whoever touches his private part, let him perform Wudu.', 'sahih', 'Jami at-Tirmidhi 64'),
('Jami at-Tirmidhi', 1, 65, 'Busrah bint Safwan', 'مَنْ مَسَّ فَرْجَهُ فَلاَ يُصَلِّ حَتَّى يَتَوَضَّأَ', 'Whoever touches his private part should not pray until he performs Wudu.', 'sahih', 'Jami at-Tirmidhi 65'),
('Jami at-Tirmidhi', 1, 66, 'Talq bin Ali', 'هَلْ هُوَ إِلاَّ بَضْعَةٌ مِنْكَ', 'Is it anything but a part of you?', 'hasan', 'Jami at-Tirmidhi 66'),
('Jami at-Tirmidhi', 1, 67, 'Aishah', 'كَانَ النَّبِيُّ يُقَبِّلُ بَعْضَ أَزْوَاجِهِ ثُمَّ يَخْرُجُ إِلَى الصَّلاَةِ وَلاَ يَتَوَضَّأُ', 'The Prophet would kiss some of his wives then go out to prayer without performing Wudu.', 'hasan', 'Jami at-Tirmidhi 67'),
('Jami at-Tirmidhi', 1, 68, 'Aishah', 'كَانَ رَسُولُ اللَّهِ يُقَبِّلُ وَهُوَ صَائِمٌ وَلاَ يَنْتَقِضُ وُضُوؤُهُ', 'The Messenger of Allah would kiss while fasting and his Wudu would not break.', 'hasan', 'Jami at-Tirmidhi 68'),
('Jami at-Tirmidhi', 1, 69, 'Abu Hurairah', 'مَنْ قَاءَ أَوْ رَعَفَ فَلْيَتَوَضَّأْ', 'Whoever vomits or has a nosebleed, let him perform Wudu.', 'daif', 'Jami at-Tirmidhi 69'),
('Jami at-Tirmidhi', 1, 70, 'Anas bin Malik', 'كَانَ النَّبِيُّ يَتَوَضَّأُ عِنْدَ كُلِّ صَلاَةٍ', 'The Prophet used to perform Wudu for every Salat.', 'sahih', 'Jami at-Tirmidhi 70'),
('Jami at-Tirmidhi', 1, 71, 'Anas bin Malik', 'صَلَّى النَّبِيُّ الصَّلَوَاتِ يَوْمَ الْفَتْحِ بِوُضُوءٍ وَاحِدٍ', 'The Prophet prayed the Salat on the Day of the Conquest with one Wudu.', 'sahih', 'Jami at-Tirmidhi 71'),
('Jami at-Tirmidhi', 1, 72, 'Aishah', 'كُنْتُ أَغْتَسِلُ أَنَا وَرَسُولُ اللَّهِ مِنْ إِنَاءٍ وَاحِدٍ', 'I and the Messenger of Allah would bathe from one vessel.', 'sahih', 'Jami at-Tirmidhi 72'),
('Jami at-Tirmidhi', 1, 73, 'Ibn Umar', 'كُنَّا نَتَوَضَّأُ نَحْنُ وَالنِّسَاءُ عَلَى عَهْدِ رَسُولِ اللَّهِ مِنْ إِنَاءٍ وَاحِدٍ', 'We and the women used to perform Wudu from one vessel during the time of the Messenger.', 'sahih', 'Jami at-Tirmidhi 73'),
('Jami at-Tirmidhi', 1, 74, 'Abu Hurairah', 'الطُّهُورُ شَطْرُ الإِيمَانِ', 'Purification is half of faith.', 'sahih', 'Jami at-Tirmidhi 74'),
('Jami at-Tirmidhi', 1, 75, 'Ammar bin Yasir', 'إِنَّمَا كَانَ يَكْفِيكَ أَنْ تَقُولَ بِيَدَيْكَ هَكَذَا', 'It would have been sufficient for you to do like this with your hands.', 'sahih', 'Jami at-Tirmidhi 75'),
('Jami at-Tirmidhi', 1, 76, 'Ammar bin Yasir', 'إِنَّمَا التَّيَمُّمُ ضَرْبَةٌ لِلْوَجْهِ وَالْيَدَيْنِ', 'Tayammum is just a strike for the face and hands.', 'sahih', 'Jami at-Tirmidhi 76'),
('Jami at-Tirmidhi', 1, 77, 'Ibn Umar', 'لاَ يَقْرَأُ الْجُنُبُ وَلاَ الْحَائِضُ شَيْئًا مِنَ الْقُرْآنِ', 'Neither the one in a state of Janabah nor the menstruating woman should recite anything from the Quran.', 'daif', 'Jami at-Tirmidhi 77'),
('Jami at-Tirmidhi', 1, 78, 'Aishah', 'كَانَ النَّبِيُّ يَذْكُرُ اللَّهَ عَلَى كُلِّ أَحْيَانِهِ', 'The Prophet used to remember Allah at all times.', 'sahih', 'Jami at-Tirmidhi 78'),
('Jami at-Tirmidhi', 1, 79, 'Aishah', 'كَانَ رَسُولُ اللَّهِ إِذَا اغْتَسَلَ مِنَ الْجَنَابَةِ بَدَأَ فَغَسَلَ يَدَيْهِ', 'When the Messenger of Allah performed Ghusl from Janabah, he started by washing his hands.', 'sahih', 'Jami at-Tirmidhi 79'),
('Jami at-Tirmidhi', 1, 80, 'Maimunah', 'وَضَعْتُ لِلنَّبِيِّ مَاءً يَغْتَسِلُ بِهِ فَأَفْرَغَ عَلَى يَدَيْهِ مَرَّتَيْنِ أَوْ ثَلاَثًا', 'I placed water for the Prophet to bathe with. He poured water on his hands two or three times.', 'sahih', 'Jami at-Tirmidhi 80'),
('Jami at-Tirmidhi', 1, 81, 'Abu Hurairah', 'إِذَا الْتَقَى الْخِتَانَانِ وَتَوَارَتِ الْحَشَفَةُ فَقَدْ وَجَبَ الْغُسْلُ', 'When the two circumcised parts meet and the head disappears, then Ghusl is obligatory.', 'sahih', 'Jami at-Tirmidhi 81'),
('Jami at-Tirmidhi', 1, 82, 'Abu Said Al-Khudri', 'إِنَّمَا الْمَاءُ مِنَ الْمَاءِ', 'Water is only from water.', 'sahih', 'Jami at-Tirmidhi 82'),
('Jami at-Tirmidhi', 1, 83, 'Abu Said Al-Khudri', 'كَانَ النَّبِيُّ إِذَا أَرَادَ أَنْ يَنَامَ وَهُوَ جُنُبٌ تَوَضَّأَ وُضُوءَهُ لِلصَّلاَةِ', 'When the Prophet wanted to sleep while in Janabah, he would perform Wudu like his Wudu for Salat.', 'sahih', 'Jami at-Tirmidhi 83'),
('Jami at-Tirmidhi', 1, 84, 'Aishah', 'كَانَ النَّبِيُّ إِذَا أَرَادَ أَنْ يَأْكُلَ أَوْ يَنَامَ وَهُوَ جُنُبٌ تَوَضَّأَ', 'When the Prophet wanted to eat or sleep while in Janabah, he would perform Wudu.', 'sahih', 'Jami at-Tirmidhi 84'),
('Jami at-Tirmidhi', 1, 85, 'Umar bin Al-Khattab', 'سَأَلْتُ النَّبِيَّ أَيَنَامُ أَحَدُنَا وَهُوَ جُنُبٌ فَقَالَ نَعَمْ إِذَا تَوَضَّأَ', 'I asked the Prophet: Can one of us sleep while in Janabah? He said: Yes, if he performs Wudu.', 'sahih', 'Jami at-Tirmidhi 85'),
('Jami at-Tirmidhi', 1, 86, 'Aishah', 'كُنْتُ أَفْرُكُ الْمَنِيَّ مِنْ ثَوْبِ رَسُولِ اللَّهِ', 'I used to scrub semen from the garment of the Messenger of Allah.', 'sahih', 'Jami at-Tirmidhi 86'),
('Jami at-Tirmidhi', 1, 87, 'Aishah', 'كُنْتُ أَغْسِلُ الْجَنَابَةَ مِنْ ثَوْبِ رَسُولِ اللَّهِ فَيَخْرُجُ إِلَى الصَّلاَةِ وَإِنَّ بُقَعَ الْمَاءِ فِي ثَوْبِهِ', 'I used to wash off Janabah from the garment of the Messenger of Allah, and he would go to prayer with water spots still on his garment.', 'sahih', 'Jami at-Tirmidhi 87'),
('Jami at-Tirmidhi', 1, 88, 'Umm Salamah', 'سَأَلَتْ أُمُّ سُلَيْمٍ النَّبِيَّ عَنِ الْمَرْأَةِ تَرَى فِي مَنَامِهَا مَا يَرَى الرَّجُلُ', 'Umm Sulaim asked the Prophet about a woman who sees in her dream what a man sees.', 'sahih', 'Jami at-Tirmidhi 88'),
('Jami at-Tirmidhi', 1, 89, 'Aishah', 'كَانَ النَّبِيُّ لاَ يَتَمَنْدَلُ بَعْدَ الْغُسْلِ', 'The Prophet did not dry himself with a cloth after Ghusl.', 'hasan', 'Jami at-Tirmidhi 89'),
('Jami at-Tirmidhi', 1, 90, 'Abu Said Al-Khudri', 'غُسْلُ يَوْمِ الْجُمُعَةِ وَاجِبٌ عَلَى كُلِّ مُحْتَلِمٍ', 'The Friday Ghusl is obligatory upon every adult.', 'sahih', 'Jami at-Tirmidhi 90'),
('Jami at-Tirmidhi', 1, 91, 'Abu Hurairah', 'غُسْلُ يَوْمِ الْجُمُعَةِ عَلَى كُلِّ مُحْتَلِمٍ', 'The Friday Ghusl is upon every adult.', 'sahih', 'Jami at-Tirmidhi 91'),
('Jami at-Tirmidhi', 1, 92, 'Samurah bin Jundub', 'مَنْ تَوَضَّأَ يَوْمَ الْجُمُعَةِ فَبِهَا وَنِعْمَتْ وَمَنِ اغْتَسَلَ فَالْغُسْلُ أَفْضَلُ', 'Whoever performs Wudu on Friday, that is sufficient, and whoever performs Ghusl, then Ghusl is better.', 'hasan', 'Jami at-Tirmidhi 92'),
('Jami at-Tirmidhi', 1, 93, 'Aishah', 'كُنَّا نُؤْمَرُ بِقَضَاءِ الصَّوْمِ وَلاَ نُؤْمَرُ بِقَضَاءِ الصَّلاَةِ', 'We were ordered to make up the fasts but not ordered to make up the prayers.', 'sahih', 'Jami at-Tirmidhi 93'),
('Jami at-Tirmidhi', 1, 94, 'Aishah', 'أَنَّ فَاطِمَةَ بِنْتَ أَبِي حُبَيْشٍ جَاءَتِ النَّبِيَّ فَقَالَتْ إِنِّي أُسْتَحَاضُ فَلاَ أَطْهُرُ', 'Fatimah bint Abi Hubaish came to the Prophet and said: I have Istihadah and do not become pure.', 'sahih', 'Jami at-Tirmidhi 94'),
('Jami at-Tirmidhi', 1, 95, 'Hamnah bint Jahsh', 'كُنْتُ أُسْتَحَاضُ حَيْضَةً كَثِيرَةً شَدِيدَةً', 'I had a severe and heavy case of Istihadah.', 'hasan', 'Jami at-Tirmidhi 95'),
('Jami at-Tirmidhi', 1, 96, 'Aishah', 'أَنَّ فَاطِمَةَ بِنْتَ أَبِي حُبَيْشٍ كَانَتْ تُسْتَحَاضُ فَأَمَرَهَا النَّبِيُّ أَنْ تَتَوَضَّأَ لِكُلِّ صَلاَةٍ', 'Fatimah bint Abi Hubaish was experiencing Istihadah, so the Prophet ordered her to perform Wudu for every Salat.', 'sahih', 'Jami at-Tirmidhi 96'),
('Jami at-Tirmidhi', 1, 97, 'Ibn Abbas', 'يَغْسِلُ الدَّمَ ثُمَّ يُصَلِّي', 'He washes the blood then prays.', 'hasan', 'Jami at-Tirmidhi 97'),
('Jami at-Tirmidhi', 1, 98, 'Anas bin Malik', 'كَانَ النَّبِيُّ يُبَاشِرُ نِسَاءَهُ فَوْقَ الإِزَارِ وَهُنَّ حُيَّضٌ', 'The Prophet would be intimate with his wives above the Izar while they were menstruating.', 'sahih', 'Jami at-Tirmidhi 98'),
('Jami at-Tirmidhi', 1, 99, 'Aishah', 'كَانَ يَأْمُرُنِي فَأَتَّزِرُ فَيُبَاشِرُنِي وَأَنَا حَائِضٌ', 'He would order me to put on the Izar then be intimate with me while I was menstruating.', 'sahih', 'Jami at-Tirmidhi 99'),
('Jami at-Tirmidhi', 1, 100, 'Aishah', 'كَانَ رَسُولُ اللَّهِ يَأْمُرُ الْحَائِضَ مِنْ نِسَائِهِ أَنْ تَتَّزِرَ', 'The Messenger of Allah would order the menstruating among his wives to put on the Izar.', 'sahih', 'Jami at-Tirmidhi 100'),
('Jami at-Tirmidhi', 1, 101, 'Umm Salamah', 'بَيْنَمَا أَنَا مَعَ النَّبِيِّ مُضْطَجِعَةٌ فِي خَمِيلَةٍ إِذْ حِضْتُ', 'While I was lying down with the Prophet under a blanket, I started menstruating.', 'sahih', 'Jami at-Tirmidhi 101'),
('Jami at-Tirmidhi', 1, 102, 'Aishah', 'لَمْ يَكُنِ النَّبِيُّ يَأْمُرُهَا بِالطَّوَافِ إِذَا كَانَتْ حَائِضًا', 'The Prophet would not order her to do Tawaf when she was menstruating.', 'sahih', 'Jami at-Tirmidhi 102'),
('Jami at-Tirmidhi', 1, 103, 'Umm Salamah', 'كَانَتِ النُّفَسَاءُ تَجْلِسُ عَلَى عَهْدِ رَسُولِ اللَّهِ أَرْبَعِينَ يَوْمًا', 'The woman in postnatal bleeding would sit for forty days during the time of the Messenger.', 'hasan', 'Jami at-Tirmidhi 103'),
('Jami at-Tirmidhi', 1, 104, 'Umm Salamah', 'كَانَتِ النُّفَسَاءُ تَقْعُدُ بَعْدَ نِفَاسِهَا أَرْبَعِينَ يَوْمًا أَوْ أَرْبَعِينَ لَيْلَةً', 'The woman in postnatal bleeding would sit after her delivery for forty days or forty nights.', 'hasan', 'Jami at-Tirmidhi 104'),
('Jami at-Tirmidhi', 1, 105, 'Aishah', 'الصُّفْرَةُ وَالْكُدْرَةُ بَعْدَ الطُّهْرِ لَيْسَتْ بِشَيْءٍ', 'Yellow and brownish discharge after purification is nothing.', 'hasan', 'Jami at-Tirmidhi 105'),
('Jami at-Tirmidhi', 1, 106, 'Umm Atiyyah', 'كُنَّا لاَ نَعُدُّ الصُّفْرَةَ وَالْكُدْرَةَ بَعْدَ الطُّهْرِ شَيْئًا', 'We did not regard the yellowish and brownish discharge after purification as anything.', 'sahih', 'Jami at-Tirmidhi 106'),
('Jami at-Tirmidhi', 1, 107, 'Ali ibn Abi Talib', 'يُنْضَحُ بَوْلُ الْغُلاَمِ وَيُغْسَلُ بَوْلُ الْجَارِيَةِ', 'The urine of a male infant is sprinkled, and the urine of a female infant is washed.', 'sahih', 'Jami at-Tirmidhi 107'),
('Jami at-Tirmidhi', 1, 108, 'Umm Qais bint Mihsan', 'جَاءَتْ بِابْنٍ لَهَا صَغِيرٍ لَمْ يَأْكُلِ الطَّعَامَ إِلَى النَّبِيِّ فَبَالَ عَلَى ثَوْبِهِ فَدَعَا بِمَاءٍ فَنَضَحَهُ', 'She came with her small son who had not yet eaten food. He urinated on the garment of the Prophet, who called for water and sprinkled it.', 'sahih', 'Jami at-Tirmidhi 108'),
('Jami at-Tirmidhi', 1, 109, 'Ibn Abbas', 'أَيُّمَا إِهَابٍ دُبِغَ فَقَدْ طَهُرَ', 'Whatever hide is tanned, it becomes pure.', 'sahih', 'Jami at-Tirmidhi 109'),
('Jami at-Tirmidhi', 1, 110, 'Salamah bin Al-Muhabbiq', 'دِبَاغُ جُلُودِ الْمَيْتَةِ طَهُورُهَا', 'Tanning the skins of dead animals is their purification.', 'sahih', 'Jami at-Tirmidhi 110'),
('Jami at-Tirmidhi', 1, 111, 'Al-Miqdam bin Madi Karib', 'نَهَى رَسُولُ اللَّهِ عَنْ جُلُودِ السِّبَاعِ', 'The Messenger of Allah prohibited the skins of predators.', 'sahih', 'Jami at-Tirmidhi 111'),
('Jami at-Tirmidhi', 1, 112, 'Abu Hurairah', 'إِذَا وَلَغَ الْكَلْبُ فِي إِنَاءِ أَحَدِكُمْ فَلْيَغْسِلْهُ سَبْعَ مَرَّاتٍ', 'When a dog licks in the vessel of one of you, wash it seven times.', 'sahih', 'Jami at-Tirmidhi 112'),
('Jami at-Tirmidhi', 1, 113, 'Abu Hurairah', 'طَهُورُ إِنَاءِ أَحَدِكُمْ إِذَا وَلَغَ فِيهِ الْكَلْبُ أَنْ يَغْسِلَهُ سَبْعَ مَرَّاتٍ أُولاَهُنَّ بِالتُّرَابِ', 'The purification of the vessel of one of you when a dog licks in it is to wash it seven times, the first of which is with soil.', 'sahih', 'Jami at-Tirmidhi 113'),
('Jami at-Tirmidhi', 1, 114, 'Abu Hurairah', 'صَلَّى فِي ثَوْبٍ وَاحِدٍ مُتَوَشِّحًا بِهِ', 'He prayed in one garment, wrapping himself in it.', 'sahih', 'Jami at-Tirmidhi 114'),
('Jami at-Tirmidhi', 1, 115, 'Umm Salamah', 'تُصَلِّي الْمَرْأَةُ فِي دِرْعٍ وَخِمَارٍ لَيْسَ عَلَيْهَا إِزَارٌ', 'A woman prays in a chemise and head cover without a lower garment.', 'hasan', 'Jami at-Tirmidhi 115'),
('Jami at-Tirmidhi', 1, 116, 'Abu Said Al-Khudri', 'نَهَى عَنِ اشْتِمَالِ الصَّمَّاءِ', 'He prohibited wrapping oneself tightly in a single garment.', 'sahih', 'Jami at-Tirmidhi 116'),
('Jami at-Tirmidhi', 1, 117, 'Anas bin Malik', 'كَانَ النَّبِيُّ يُصَلِّي عَلَى الْحَصِيرِ', 'The Prophet used to pray on a mat.', 'sahih', 'Jami at-Tirmidhi 117'),
('Jami at-Tirmidhi', 1, 118, 'Abu Said Al-Khudri', 'صَلَّى النَّبِيُّ فِي نَعْلَيْهِ', 'The Prophet prayed in his sandals.', 'sahih', 'Jami at-Tirmidhi 118'),
('Jami at-Tirmidhi', 1, 119, 'Anas bin Malik', 'كَانَ يُصَلِّي عَلَى الأَرْضِ', 'He used to pray on the ground.', 'sahih', 'Jami at-Tirmidhi 119'),
('Jami at-Tirmidhi', 1, 120, 'Jabir bin Abdullah', 'جُعِلَتْ لِيَ الأَرْضُ مَسْجِدًا وَطَهُورًا', 'The earth has been made for me a place of prayer and a means of purification.', 'sahih', 'Jami at-Tirmidhi 120'),
('Jami at-Tirmidhi', 1, 121, 'Abu Hurairah', 'الْفِطْرَةُ خَمْسٌ الاِخْتِتَانُ وَالاِسْتِحْدَادُ وَقَصُّ الشَّارِبِ وَتَقْلِيمُ الأَظْفَارِ وَنَتْفُ الآبَاطِ', 'Five are from the Fitrah: circumcision, shaving the pubic area, trimming the mustache, clipping nails, and plucking the armpit hair.', 'sahih', 'Jami at-Tirmidhi 121'),
('Jami at-Tirmidhi', 1, 122, 'Abu Hurairah', 'الْفِطْرَةُ خَمْسٌ أَوْ خَمْسٌ مِنَ الْفِطْرَةِ', 'Five things are from the Fitrah or the Fitrah is five things.', 'sahih', 'Jami at-Tirmidhi 122'),
('Jami at-Tirmidhi', 1, 123, 'Uthaimah bin Uthman', 'أَنَّ إِبْرَاهِيمَ اخْتَتَنَ وَهُوَ ابْنُ ثَمَانِينَ سَنَةً بِالْقَدُومِ', 'Ibrahim was circumcised when he was eighty years old, with an adze.', 'sahih', 'Jami at-Tirmidhi 123'),
('Jami at-Tirmidhi', 1, 124, 'Ibn Umar', 'قُصُّوا الشَّوَارِبَ وَأَعْفُوا اللِّحَى', 'Trim the mustaches and leave the beards to grow.', 'sahih', 'Jami at-Tirmidhi 124'),
('Jami at-Tirmidhi', 1, 125, 'Abu Hurairah', 'إِنَّ الْيَهُودَ وَالنَّصَارَى لاَ يَصْبُغُونَ فَخَالِفُوهُمْ', 'Indeed the Jews and Christians do not dye their hair, so differ from them.', 'sahih', 'Jami at-Tirmidhi 125'),
('Jami at-Tirmidhi', 1, 126, 'Abu Hurairah', 'غَيِّرُوا الشَّيْبَ وَلاَ تَشَبَّهُوا بِالْيَهُودِ', 'Change the gray hair and do not imitate the Jews.', 'hasan', 'Jami at-Tirmidhi 126'),
('Jami at-Tirmidhi', 1, 127, 'Anas bin Malik', 'رَأَيْتُ شَعَرَ رَسُولِ اللَّهِ مَخْضُوبًا', 'I saw the hair of the Messenger of Allah dyed.', 'sahih', 'Jami at-Tirmidhi 127'),
('Jami at-Tirmidhi', 1, 128, 'Anas bin Malik', 'أَنَّ النَّبِيَّ لَمْ يَخْضِبْ إِنَّمَا كَانَ الْبَيَاضُ فِي عَنْفَقَتِهِ', 'The Prophet did not dye. The white hair was only in his lower lip.', 'sahih', 'Jami at-Tirmidhi 128'),
('Jami at-Tirmidhi', 1, 129, 'Abu Rimthah', 'رَأَيْتُ النَّبِيَّ وَقَدْ لَطَخَ لِحْيَتَهُ بِالصُّفْرَةِ', 'I saw the Prophet, and he had painted his beard with yellow dye.', 'hasan', 'Jami at-Tirmidhi 129'),
('Jami at-Tirmidhi', 1, 130, 'Ibn Umar', 'أَعْفُوا اللِّحَى وَحُفُّوا الشَّوَارِبَ', 'Leave the beards and trim the mustaches.', 'sahih', 'Jami at-Tirmidhi 130'),
('Jami at-Tirmidhi', 1, 131, 'Aishah', 'كَانَ النَّبِيُّ يُحِبُّ التَّيَمُّنَ فِي كُلِّ شَيْءٍ', 'The Prophet loved starting with the right in everything.', 'sahih', 'Jami at-Tirmidhi 131'),
('Jami at-Tirmidhi', 1, 132, 'Abdullah bin Mughaffal', 'نَهَى النَّبِيُّ عَنِ التَّرَجُّلِ إِلاَّ غِبًّا', 'The Prophet prohibited combing hair except occasionally.', 'hasan', 'Jami at-Tirmidhi 132'),
('Jami at-Tirmidhi', 1, 133, 'Anas bin Malik', 'كَانَ شَعَرُ النَّبِيِّ إِلَى نِصْفِ أُذُنَيْهِ', 'The hair of the Prophet was to the middle of his ears.', 'sahih', 'Jami at-Tirmidhi 133'),
('Jami at-Tirmidhi', 1, 134, 'Umm Habibah', 'سَأَلَتِ النَّبِيَّ عَنِ الدَّمِ فَأَمَرَهَا أَنْ تَغْتَسِلَ', 'She asked the Prophet about blood, and he ordered her to perform Ghusl.', 'sahih', 'Jami at-Tirmidhi 134'),
('Jami at-Tirmidhi', 1, 135, 'Ibn Masud', 'كُنَّا لاَ نَتَوَضَّأُ مِنْ مَوْطِئٍ', 'We would not perform Wudu from stepping on something impure.', 'hasan', 'Jami at-Tirmidhi 135'),
('Jami at-Tirmidhi', 1, 136, 'Anas bin Malik', 'كَانَ النَّبِيُّ يُصَلِّي فِي النَّعْلَيْنِ', 'The Prophet used to pray wearing sandals.', 'sahih', 'Jami at-Tirmidhi 136'),
('Jami at-Tirmidhi', 1, 137, 'Abu Hurairah', 'جُعِلَتْ لِيَ الأَرْضُ مَسْجِدًا وَطَهُورًا فَأَيُّمَا رَجُلٍ أَدْرَكَتْهُ الصَّلاَةُ فَلْيُصَلِّ حَيْثُ أَدْرَكَتْهُ', 'The earth was made for me as a masjid and purifier. Wherever a man finds the prayer time, let him pray where he is.', 'sahih', 'Jami at-Tirmidhi 137'),
('Jami at-Tirmidhi', 1, 138, 'Ibn Abbas', 'عَشْرٌ مِنَ الْفِطْرَةِ', 'Ten are from the Fitrah.', 'sahih', 'Jami at-Tirmidhi 138'),
('Jami at-Tirmidhi', 1, 139, 'Abu Hurairah', 'خَمْسٌ مِنَ الْفِطْرَةِ الاِخْتِتَانُ وَالاِسْتِحْدَادُ', 'Five are from the Fitrah: circumcision and shaving the pubic area.', 'sahih', 'Jami at-Tirmidhi 139'),
('Jami at-Tirmidhi', 1, 140, 'Abu Hurairah', 'إِنَّ أَبَا هُرَيْرَةَ كَانَ يَقُصُّ شَارِبَهُ', 'Abu Hurairah used to trim his mustache.', 'sahih', 'Jami at-Tirmidhi 140'),
('Jami at-Tirmidhi', 1, 141, 'Anas bin Malik', 'وُقِّتَ لَنَا فِي قَصِّ الشَّارِبِ وَتَقْلِيمِ الأَظْفَارِ أَنْ لاَ نَتْرُكَ أَكْثَرَ مِنْ أَرْبَعِينَ يَوْمًا', 'A time was set for us for trimming the mustache, clipping nails: not more than forty days.', 'sahih', 'Jami at-Tirmidhi 141'),
('Jami at-Tirmidhi', 1, 142, 'Abu Hurairah', 'اخْتَتَنَ إِبْرَاهِيمُ خَلِيلُ الرَّحْمَنِ بَعْدَ ثَمَانِينَ سَنَةً', 'Ibrahim, the Khalil of the Most Merciful, was circumcised after eighty years.', 'sahih', 'Jami at-Tirmidhi 142'),
('Jami at-Tirmidhi', 1, 143, 'Abu Hurairah', 'اخْضِبُوا بِالْحِنَّاءِ وَالْكَتَمِ', 'Dye with henna and katam.', 'hasan', 'Jami at-Tirmidhi 143'),
('Jami at-Tirmidhi', 1, 144, 'Abu Dharr', 'إِنَّ أَحْسَنَ مَا غُيِّرَ بِهِ الشَّيْبُ الْحِنَّاءُ وَالْكَتَمُ', 'The best thing to change gray hair is henna and katam.', 'sahih', 'Jami at-Tirmidhi 144'),
('Jami at-Tirmidhi', 1, 145, 'Abu Hurairah', 'إِنَّ النَّبِيَّ نَهَى عَنِ الدُّبَّاءِ وَالْحَنْتَمِ وَالْمُزَفَّتِ', 'The Prophet prohibited Ad-Dubba, Al-Hantam, and Al-Muzaffat.', 'sahih', 'Jami at-Tirmidhi 145'),
('Jami at-Tirmidhi', 1, 146, 'Ibn Abbas', 'نَهَى عَنِ الدُّبَّاءِ وَالنَّقِيرِ وَالْمُزَفَّتِ وَالْحَنْتَمِ', 'He prohibited Ad-Dubba, An-Naqir, Al-Muzaffat, and Al-Hantam.', 'sahih', 'Jami at-Tirmidhi 146'),
('Jami at-Tirmidhi', 1, 147, 'Ibn Umar', 'نَهَى عَنِ الْجَرِّ وَالدُّبَّاءِ وَالْمُزَفَّتِ', 'He prohibited Al-Jarr, Ad-Dubba, and Al-Muzaffat.', 'sahih', 'Jami at-Tirmidhi 147'),
('Jami at-Tirmidhi', 1, 148, 'Abu Hurairah', 'نَهَى رَسُولُ اللَّهِ عَنِ الدُّبَّاءِ وَالْحَنْتَمِ', 'The Messenger of Allah prohibited Ad-Dubba and Al-Hantam.', 'sahih', 'Jami at-Tirmidhi 148');
